void createFileFromHex(char* p, FILE* fp) // léo
{
    unsigned char value;
    for(int i = 0; i <= strlen(p)/3; i++)
    {
        value = strtol(p+3*i, NULL, 16);
        fwrite(&value, sizeof(value), 1, fp);
    }
}

